var wsh = require('wshelper');

/**
 * This function receive config object
 * It is called before service
 * @param oConfig program configuration (json stored in the config/ directory
 */
function init(oConfig) {
}

/**
 * this function run the game
 */
function service(oSocket) {
	var sAddress = wsh.getAddress(oSocket);
	console.log('welcome to new client :', sAddress);

	// Disconnection event
	oSocket.on('disconnect', function() {
		console.log('goodbye', sAddress);
	});
}


module.exports = {
	init: init,
	service: service
};
