var O2 = require('o2');
var Mediator = require('mediator');

var TimeMessagePlugin = O2.extendClass(Mediator.Plugin, {
	
	getName: function() {
		return 'TimeMessage';
	},
	
	init: function() {
		this.register('message');
	},
	
	message: function(ctx) {
		var d = new Date();
		ctx.time = [d.getHours(), d.getMinutes()];	
	}
});

module.exports = TimeMessagePlugin;
