var FilterPlugin = require('../Plugins/FilterPlugin.js');

test('basic', function(assert) {
	var f = new FilterPlugin();
	f.setWords({gayzor:'######'});
	assert.ok(f, 'Filter plugin ok');
	assert.equal(f.processFilter('xxxx'), 'xxxx', 'no filter');
	assert.equal(f.processFilter('xxxx gAyZoR'), 'xxxx ######', 'filtered');
});

